    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        if (!emptyFields()) {
            String nome = txtNome.getText();
            String login = txtLogin.getText();
            String senha = txtSenha.getText();
            String permissao = cbPermissao.getSelectedItem().toString();

            Usuarios usuario = new Usuarios();

            usuario.setNome(nome);
            usuario.setLogin(login);
            usuario.setSenha(senha);
            usuario.setPermissao(permissao);

            UsuarioDAO usuarioDao = new UsuarioDAO();
            usuarioDao.inserirUsuario(usuario);

            JOptionPane.showMessageDialog(null, "Cadastro realizado com sucesso!");
            LimparCampos();
        }

    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnVerListaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerListaActionPerformed
        ListaUsuarios listausuarios = new ListaUsuarios();
        listausuarios.validaPermissao(permissao);
        dispose();
        listausuarios.setVisible(true);

    }//GEN-LAST:event_btnVerListaActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
        if (!emptyFields()) {
            int id = Integer.parseInt(txtID.getText());
            String nome = txtNome.getText();
            String login = txtLogin.getText();
            String senha = txtSenha.getText();
            String permissaoU = cbPermissao.getSelectedItem().toString();

            Usuarios usuario = new Usuarios();

            usuario.setId(id);
            usuario.setNome(nome);
            usuario.setLogin(login);
            usuario.setSenha(senha);
            usuario.setPermissao(permissaoU);

            UsuarioDAO usuarioDao = new UsuarioDAO();
            usuarioDao.editar(usuario);

            JOptionPane.showMessageDialog(null, "Cadastro Atualizado com sucesso!");
            LimparCampos();
        }
    }//GEN-LAST:event_btnAtualizarActionPerformed

    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        if (txtIdpesquisa.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Informe um ID");
        } else {
            int idPesquisa = Integer.parseInt(txtIdpesquisa.getText());
            UsuarioDAO usuarioDao = new UsuarioDAO();
            Usuarios usuario = usuarioDao.getUsuario(idPesquisa);

            if (usuario == null) {
                JOptionPane.showMessageDialog(this, "Usuário não encontrado!");
            } else {
                txtID.setText(String.valueOf(usuario.getId()));
                txtNome.setText(usuario.getNome());
                txtLogin.setText(usuario.getLogin());
                txtSenha.setText(usuario.getSenha());
                cbPermissao.setSelectedItem(usuario.getPermissao());
            }
        }
    }//GEN-LAST:event_btnPesquisarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        int id = Integer.parseInt(txtID.getText());

        UsuarioDAO usuarioDao = new UsuarioDAO();
        usuarioDao.Excluir(id);

        LimparCampos();

    }//GEN-LAST:event_btnExcluirActionPerformed

