    public void LimparCampos() {
        txtIdpesquisa.setText("");
        txtID.setText("");
        txtNome.setText("");
        txtLogin.setText("");
        txtSenha.setText("");
        cbPermissao.setSelectedItem("Selecione");
        txtNome.requestFocus();

    }

    private boolean emptyFields() {

        boolean empty = true;

        if (txtNome.getText().trim().isEmpty()) {

            JOptionPane.showMessageDialog(rootPane, "ATENÇÃO! Nome não pode ser vazio.");
        } else if (txtLogin.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "ATENÇÃO! Login não pode ser vazio.");
        } else if (txtSenha.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "ATENÇÃO! Senha não pode ser vazio.");
        } else if (cbPermissao.getSelectedItem().toString().equals("Selecione")) {
            JOptionPane.showMessageDialog(rootPane, "ATENÇÃO! Selecione uma Permissão.");
        } else {
            empty = false;
        }
        return empty;
    }


    public void PegaIDUsuario(int id) {
        // Preencha os campos da tela de cadastro com o ID do cliente
        txtIdpesquisa.setText(String.valueOf(id));
    }
    
       public void validaPermissao(String permissao) {
        this.permissao = permissao;
        if (permissao.equals("Projetista")) {
            btnCadastrar.setEnabled(false);
            btnAtualizar.setEnabled(false);
            btnExcluir.setEnabled(false);
        } else if (permissao.equals("Vendedor")) {
            btnAtualizar.setEnabled(false);
            btnExcluir.setEnabled(false);
        }
    }
    
